<script setup lang="ts">

// TODO orientation correction is a working garbage.

import { EXIF } from "exif-js";

const name = ref("");
const description = ref("");
const formData = ref(undefined);
const state = ref("input");
const errorMsg = ref("");

// define upload image
const { data, pending, error, execute } = useFetch('/api/images', {
  method: 'POST',
  immediate: false,
  body: formData,
});

// fetch districts data
const { data: districtsData } = useFetch('/api/districts');

// update state on pending or error changes
watch([pending, error], ([newPendingValue, newErrorValue]) => {

  if (newErrorValue) {

    if (newErrorValue.data?.error) {
      errorMsg.value = newErrorValue.data.error;
    } else {
      errorMsg.value = newErrorValue.message;
    }

    state.value = "error";
  } else if (newPendingValue) {
    state.value = "loading";
  } else {
    state.value = "input";
  }


})

async function newFiles(files) {

  if (files.length > 1) {
    console.warn("Only single file upload is supported for now.");
  }

  const file = files[0];

  correctImageOrientation(file);

}

async function correctImageOrientation(file) {
  const reader = new FileReader();
  var correctedFile;

  reader.onload = function (readerEvent) {

    console.log("Reader loaded", readerEvent);

    const img = new Image();
    img.onload = function (imageEvent) {

      const orientation = EXIF.getTag(imageEvent.target, "Orientation");

      console.log("orientation", String(orientation));

      correctedFile = fixOrientation(imageEvent.target, 0)

    }
    img.src = readerEvent.target.result;
  }

  reader.readAsDataURL(file);
}

function fixOrientation(img, orientation) {

  const canvas = document.createElement('canvas');
  const ctx = canvas.getContext('2d');

  // Set proper canvas dimensions before transform & export
  if (4 < orientation && orientation < 9) {
    canvas.width = img.height;
    canvas.height = img.width;
  } else {
    canvas.width = img.width;
    canvas.height = img.height;
  }

  // Transform context before drawing image
  switch (orientation) {
    case 2: ctx.transform(-1, 0, 0, 1, img.width, 0); break;
    case 3: ctx.transform(-1, 0, 0, -1, img.width, img.height); break;
    case 4: ctx.transform(1, 0, 0, -1, 0, img.height); break;
    case 5: ctx.transform(0, 1, 1, 0, 0, 0); break;
    case 6: ctx.transform(0, 1, -1, 0, img.height, 0); break;
    case 7: ctx.transform(0, -1, -1, 0, img.height, img.width); break;
    case 8: ctx.transform(0, -1, 1, 0, 0, img.width); break;
    default: break;
  }

  // Draw the image on the canvas
  ctx.drawImage(img, 0, 0);

  // Convert canvas to Blob
  canvas.toBlob((blob) => {
    // Create a new File object
    const correctedFile = new File([blob], file.name, { type: file.type });

    // You can now use this corrected file for upload
    console.log("Image orientation fixed and ready for upload");
    console.log(correctedFile);

    const tmpFormData = new FormData();
    tmpFormData.append('file', correctedFile);
    formData.value = tmpFormData;

  }, file.type);
}

async function create() {

  const { id } = await $fetch("/api/myitems", {
    method: "POST",
    body: { name: name.value, description: description.value, image: data?.value.filename },
  });
  await navigateTo(`/mystuff#item-${id}`);

}

